
public class variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* variables are containers for storing data values. in java, there are different types of variables, for example:
		 * string-store text
		 * int-stores integers
		 * float-stores floating point numbers
		 * char-stores single characters
		 * boolean-stores values with two states
		
		 *  */
		String name = "Mark"; //THIS CODE WILL CREATE A STRING
		System.out.println(name);

		int myNum = 5;
		System.out.println(myNum);

		float myFloatNum = 5.99f;
		System.out.println(myFloatNum);

		char myLetter = 'D';
		System.out.println(myLetter);

		boolean myBool = true;
		System.out.println(myBool);

		String myText = "Hello";
		System.out.println(myText);

		String Name = "Mark";
		System.out.println("Hello " + Name);
		
		String firstName = "Mark ";
		String lastName = "Sikaundi";
		String fullName = firstName + lastName;
		System.out.println(fullName);
		
	}

}
